INSERT INTO `{prefix}config` VALUES ('1502', '自助升级@gradebuy', '11', '参数配置', 'config', '{\"1\":{\"value\":\"10\",\"currency\":\"jifen\"},\"2\":{\"value\":\"100\",\"currency\":\"jifen\"},\"3\":{\"value\":\"500\",\"currency\":\"jifen\"},\"4\":{\"value\":\"5000\",\"currency\":\"jifen\"}}', 'json', 'form_custom', '{\"html\":\"{php}\\r\\n$grades = \\\\ebcms\\\\Config::get(\'user.base.grade\');\\r\\n$currency = \\\\ebcms\\\\Config::get(\'user.base.currency\');\\r\\n$res = [];\\r\\n$_value = json_decode($field[\'value\'],true);\\r\\nforeach($grades as $k =&gt; $title){\\r\\n\\t$res[] = [\\r\\n\\t\\t\'title\'=&gt;$title,\\r\\n\\t\\t\'field\'=&gt;$field[\'field\'].\'[\'.$k.\']\',\\r\\n\\t\\t\'value\'=&gt; isset($_value[$k][\'value\'])?$_value[$k][\'value\']:\'\',\\r\\n\\t\\t\'currency\'=&gt; isset($_value[$k][\'currency\'])?$_value[$k][\'currency\']:\'jifen\',\\r\\n\\t];\\r\\n}\\r\\n{\\/php}\\r\\n{foreach name=\'res\' item=\'vo\'}\\r\\n&lt;div class=&quot;row&quot; style=&quot;padding-bottom:15px;&quot;&gt;\\r\\n\\t&lt;div class=&quot;col-md-3&quot;&gt;\\r\\n\\t\\t\\u5347\\u7ea7\\u5230&lt;b&gt;\\u3010{$vo.title}\\u3011&lt;\\/b&gt;\\u9700\\u8981\\r\\n\\t&lt;\\/div&gt;\\r\\n\\t&lt;div class=&quot;col-md-3&quot;&gt;\\r\\n\\t\\t&lt;input type=&quot;text&quot; class=&quot;form-control&quot; id=&quot;{$field.unique}_{$key}&quot; name=&quot;{$vo.field}[value]&quot; value=&quot;{$vo.value}&quot; placeholder=&quot;&quot;&gt;\\r\\n\\t&lt;\\/div&gt;\\r\\n\\t&lt;div class=&quot;col-md-6&quot;&gt;\\r\\n\\t\\t{foreach name=\'currency\' item=\'cu\' key=\'cur\'}\\r\\n\\t\\t\\t&lt;label class=&quot;radio-inline&quot; for=&quot;{$field.unique}_{$key}_{$cur}&quot;&gt;\\r\\n\\t\\t\\t\\t&lt;input type=&quot;radio&quot; id=&quot;{$field.unique}_{$key}_{$cur}&quot; name=&quot;{$vo.field}[currency]&quot; value=&quot;{$cur}&quot; {eq name=&quot;vo.currency&quot; value=\'$cur\'}checked{\\/eq}&gt; {$cu}\\r\\n\\t\\t\\t&lt;\\/label&gt;\\r\\n\\t\\t{\\/foreach}\\r\\n\\t&lt;\\/div&gt;\\r\\n&lt;\\/div&gt;\\r