INSERT INTO `{prefix}formfield` VALUES ('17492', '2542', '基本信息', '权限', 'rule_ids', '', '', '0', '', 'form_custom', '{\"html\":\"{php}\\r\\n    $checked = \\\\think\\\\Db::name(\'auth_group\')-&gt;where(array(\'id\' =&gt; array(\'eq\', input(\'id\'))))-&gt;value(\'rules\');\\r\\n    $rules = \\\\think\\\\Db::name(\'auth_rule\')-&gt;order(\'sort desc\')-&gt;select();\\r\\n    $rules = \\\\ebcms\\\\Tree::tree($rules);\\r\\n{\\/php}\\r\\n{foreach name=\'rules\' item=\'a\'}\\r\\n    &lt;table class=&quot;table table-bordered&quot;&gt;\\r\\n        &lt;tr&gt;\\r\\n            &lt;td colspan=&quot;2&quot;&gt;\\r\\n                &lt;label class=&quot;checkbox-inline&quot;&gt;\\r\\n                    &lt;input type=&quot;checkbox&quot; name=&quot;{$field.field}[]&quot; value=&quot;{$a.id}&quot; {in name=&quot;a.id&quot; value=&quot;$checked&quot;}checked=&quot;checked&quot;{\\/in}&gt; {$a.title}\\r\\n                &lt;\\/label&gt;\\r\\n            &lt;\\/td&gt;\\r\\n        &lt;\\/tr&gt;\\r\\n        {foreach name=\'a.rows\' item=\'b\'}\\r\\n        &lt;tr&gt;\\r\\n            &lt;th&gt;\\r\\n                &lt;label class=&quot;checkbox-inline&quot;&gt;\\r\\n                    &lt;input type=&quot;checkbox&quot; name=&quot;{$field.field}[]&quot; value=&quot;{$b.id}&quot; {in name=&quot;b.id&quot; value=&quot;$checked&quot;}checked=&quot;checked&quot;{\\/in}&gt; {$b.title}\\r\\n                &lt;\\/label&gt;\\r\\n            &lt;\\/th&gt;\\r\\n            &lt;td&gt;\\r\\n                {foreach name=\'b.rows\' item=\'c\'}\\r\\n                &lt;label class=&quot;checkbox-inline&quot;&gt;\\r\\n                    &lt;input type=&quot;checkbox&quot; name=&quot;{$field.field}[]&quot; value=&quot;{$c.id}&quot; {in name=&quot;c.id&quot; value=&quot;$checked&quot;}checked=&quot;checked&quot;{\\/in}&gt; {$c.title}\\r\\n                &lt;\\/label&gt;\\r\\n                {\\/foreach}\\r\\n            &lt;\\/td&gt;\\r\\n        &lt;\\/tr&gt;\\r\\n        {\\/foreach}\\r\\n    &lt;\\/table&gt;\\r\\n{\\/foreach}\"}', '', '9');
