INSERT INTO `{prefix}formfield` VALUES ('22418', '3180', '基本信息', '关键字', 'keywords', '', '', '3', 'keywords', 'form_custom', '{\"html\":\"&lt;script&gt;\\r\\n    $(function() {\\r\\n        EBCMS.CORE.myfun(\'suggest_keywords\',function(container,content,strong){\\r\\n            if (content) {\\r\\n                if (content != 0) {\\r\\n                    if ($(container).val()) {\\r\\n                        var keywords = $(container).val().split(\',\');\\r\\n                        var ue = UE.getEditor(content);\\r\\n                        var contents = ue.getContent().split(\'\\uff0c\');\\r\\n                        var num = Math.min(keywords.length,contents.length-1);\\r\\n                        var nums = EBCMS.FN.random_num(num,0,contents.length-1);\\r\\n                        $.each(nums, function(index, val) {\\r\\n                            if (strong == 1) {\\r\\n                                contents[val] = contents[val]+\'\\uff0c&lt;strong&gt;\'+keywords[index]+\'&lt;\\/strong&gt;\';\\r\\n                            }else{\\r\\n                                contents[val] = contents[val]+\'\\uff0c\'+keywords[index];\\r\\n                            }\\r\\n                        });\\r\\n                        ue.setContent(contents.join(\'\\uff0c\'));\\r\\n                    }\\r\\n                }else{\\r\\n                    EBCMS.MSG.notice(\'\\u672a\\u914d\\u7f6e\\u63a5\\u6536\\u5b57\\u6bb5\\uff01\');\\r\\n                }\\r\\n            }else{\\r\\n                EBCMS.MSG.tips(\'\\u83b7\\u53d6\\u4e2d...\');\\r\\n                $.ajax({\\r\\n                    url: \'{:url(\'ebcms\\/api\\/index\')}\',\\r\\n                    type: \'POST\',\\r\\n                    dataType: \'json\',\\r\\n                    data: {\\r\\n                        api: \'suggest_keywords\',\\r\\n                        k:$(container).val(),\\r\\n                    },\\r\\n                    success:function(res){\\r\\n                        if (res.data &amp;&amp; res.data.length) {\\r\\n                            EBCMS.MSG.tips(\'\\u83b7\\u53d6\\u6210\\u529f...\',0.5);\\r\\n                            $(container).val($(container).val()+\',\'+res.data.join(\',\'));\\r\\n                        }else{\\r\\n                            EBCMS.MSG.tips(\'\\u6682\\u65e0\\u5185\\u5bb9...\');\\r\\n                        }\\r\\n                    },\\r\\n                    error:function(){\\r\\n                        EBCMS.MSG.alert(\'\\u670d\\u52a1\\u5668\\u5fd9\\uff0c\\u8bf7\\u7a0d\\u540e\\u518d\\u8bd5\\uff01\');\\r\\n                    }\\r\\n                });\\r\\n            }\\r\\n        });\\r\\n    });\\r\\n&lt;\\/script&gt;\\r\\n&lt;table class=&quot;table&quot; style=&quot;margin-bottom: 0px;&quot;&gt;\\r\\n    &lt;tr&gt;\\r\\n        &lt;td style=&quot;padding: 0px !important;border-top:none;&quot;&gt;\\r\\n            &lt;input id=&quot;{$field.unique}&quot; class=&quot;form-control&quot; name=&quot;{$field.field}&quot; value=&quot;{$field.value}&quot; placeholder=&quot;{$field.config.prompt|default=\'\'}&quot; {eq name=\'field.config.disabled\' value=\'1\'} disabled{\\/eq} {eq name=\'field.config.readonly\' value=\'1\'} readonly{\\/eq}\\/&gt;\\r\\n        &lt;\\/td&gt;\\r\\n        &lt;td style=&quot;width: 100px;padding: 0px 0px 0px 10px !important;border-top:none;&quot;&gt;\\r\\n            &lt;button type=&quot;button&quot; class=&quot;btn btn-primary btn-block&quot; onclick=&quot;EBCMS.MYFUN.suggest_keywords(\'#{$field.unique}\');&quot;&gt;\\u957f\\u5c3e\\u5173\\u952e\\u8bcd&lt;\\/button&gt;\\r\\n        &lt;\\/td&gt;\\r\\n        &lt;td style=&quot;width: 100px;padding: 0px 0px 0px 10px !important;border-top:none;&quot;&gt;\\r\\n            &lt;button type=&quot;button&quot; class=&quot;btn btn-primary btn-block&quot; onclick=&quot;EBCMS.MYFUN.suggest_keywords(\'#{$field.unique}\',\'{$groups[\'\\u57fa\\u672c\\u4fe1\\u606f\'][\'body[body]\'][\'unique\']}\',1);&quot;&gt;\\u63d2\\u5165\\u5230\\u5185\\u5bb9&lt;\\/button&gt;\\r\\n        &lt;\\/td&gt;\\r\\n    &lt;\\/tr&gt;\\r\\n&lt;\\/table&gt;\"}', '', '60');
