INSERT INTO `{prefix}mbzs` VALUES ('5', '广告', '论坛帖子', 'index_forum', '{if condition=&quot;check_app(\'forum\')&quot;}\r\n&lt;style&gt;\r\n	.text-overflow{\r\n		display:block;\r\n		white-space:nowrap;\r\n		overflow:hidden;\r\n		text-overflow:ellipsis;\r\n	}\r\n&lt;/style&gt;\r\n&lt;div class=&quot;row&quot;&gt;\r\n	&lt;div class=&quot;col-md-12&quot;&gt;\r\n		{php}\r\n			$lists = \\app\\forum\\model\\Thread::with(\'forum,user\') -&gt; where([\'status\'=&gt;1,\'forum_id\'=&gt;[\'in\',\'1,2\']]) -&gt; order(\'id desc,rethread_time desc\') -&gt; limit(15) -&gt; select();\r\n		{/php}\r\n		&lt;table class=&quot;table table-bordered table-hover&quot; style=&quot;table-layout: fixed;&quot;&gt;\r\n			&lt;tr&gt;\r\n				&lt;th style=&quot;width: 130px;&quot;&gt;作者&lt;/th&gt;\r\n				&lt;th&gt;标题&lt;/th&gt;\r\n				&lt;th style=&quot;width: 140px;&quot; class=&quot;hidden-xs&quot;&gt;发帖时间&lt;/th&gt;\r\n				&lt;th style=&quot;width: 50px;&quot; class=&quot;hidden-xs&quot;&gt;阅读&lt;/th&gt;\r\n				&lt;th style=&quot;width: 50px;&quot; class=&quot;hidden-xs&quot;&gt;跟帖&lt;/th&gt;\r\n				&lt;th style=&quot;width: 140px;&quot; class=&quot;hidden-xs&quot;&gt;最后回帖&lt;/th&gt;\r\n			&lt;/tr&gt;\r\n			{foreach name=\'lists\' item=\'vo\'}\r\n			&lt;tr&gt;\r\n				&lt;td&gt;{notempty name=\'vo.user.nickname\'}&lt;a href=&quot;{$vo.user.space_url}&quot; class=&quot;text-overflow&quot;&gt;&lt;img src=&quot;{$vo.user.avatar|thumb=14,14,3}&quot; width=&quot;14&quot; height=&quot;14&quot; alt=&quot;&quot;&gt; {$vo.user.nickname}&lt;/a&gt;{else}&lt;img src=&quot;/system/image/avatar.gif&quot; width=&quot;14&quot; height=&quot;14&quot; alt=&quot;&quot;&gt; 游客{/notempty}&lt;/td&gt;\r\n				&lt;td&gt;&lt;a href=&quot;{$vo.forum.url}&quot; class=&quot;text-muted&quot;&gt;[{$vo.forum.title}]&lt;/a&gt;&amp;nbsp;&lt;a href=&quot;{$vo.url}&quot; style=&quot;{$vo.style}&quot;&gt;{$vo.title}&lt;/a&gt;&lt;/td&gt;\r\n				&lt;td class=&quot;hidden-xs&quot;&gt;{$vo.create_time|date=\'Y-m-d H:i\',###}&lt;/td&gt;\r\n				&lt;td class=&quot;hidden-xs&quot;&gt;{$vo.views}&lt;/td&gt;\r\n				&lt;td class=&quot;hidden-xs&quot;&gt;{$vo.rethreads2}&lt;/td&gt;\r\n				&lt;td class=&quot;hidden-xs&quot;&gt;{$vo.rethread_time|date=\'Y-m-d H:i\',###}&lt;/td&gt;\r\n			&lt;/tr&gt;\r\n			{/foreach}\r\n		&lt;/table&gt;\r\n	&lt;/div&gt;\r\n&lt;/div&gt;\r\n{/if}', 'tpl', '', '1501410271', '1501410271', '0', '1');
